import React, { Component } from 'react'
import {Redirect} from 'react-router-dom'
import {connect} from 'react-redux'

class Profile extends Component {
    update = (e) => {
        e.preventDefault();
        console.log("heck")
        return <Redirect to='/updateaccount' />
    }
    render() {
        const {auth, profile} = this.props; 
        console.log(profile)
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <div className="card">
                    <div className="card-content">
                        <h4 className="card-title">Your Profile</h4>
                        <span className="card-title">Name: {profile.firstName+" "+profile.lastName}</span>
                        <p>Email: {profile.email}</p>
                        <p>Phone Number: {profile.phoneNo}</p>
                        <p>Account Type: {profile.userType}</p>
                        {profile.userType === "trainer" &&
                            <div><p>Skills: </p>
                                {profile.skills && profile.skills.map(skill => {
                                    return (
                                        <p>Skill: {skill.skill} - Skill Level: {skill.skillLvl}</p>
                                    )
                                })}
                            </div>}
                        <br/>
                        <div className="input-field">
                            <button className="btn blue lighten-1" type="button" onclick={this.update}>Update Account Information</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth,
        profile: state.firebase.profile
    }
}

export default connect(mapStateToProps)(Profile)
