import React, {Component} from "react";
import "react-big-calendar/lib/css/react-big-calendar.css";
import {Calendar,DateLocalizer,momentLocalizer,globalizeLocalizer,move,Views,Navigate,components,} from 'react-big-calendar';
import moment from 'moment';
import Modal from "react-bootstrap/Modal";
import{ Button }from 'react-bootstrap'

moment.locale('en-GB');
const localizer = momentLocalizer(moment)

const Cal =(props)=>{

  // CourseInfo = (selectedCourse) => {
  //   console.log(selectedCourse)
  //   return (
  //     <Redirect to={{
  //       pathname: '/courseinfo',
  //       course: { selectedCourse }
  //       }}
  //     />
  //   )
  // }
  const modalStyle = () => {
    return {
      background: "black",
      position: "relative",
      top: "50%",
      transform: "translateY(-50%)"
    }
  }

  const handleSelect = (event) => {
    setModalShow(true)
    console.log(event)
    setEvent(event)
    var startDate = event.start.toString()
    setStart(startDate)
    var endDate = event.end.toString()
    setEnd(endDate)
  }

  function courseFormat () {
    var courses = props.courses
    var formattedCourses = []
    var i = 0
    courses && courses.forEach(course => {
      formattedCourses[i] = {
        'title' : course.title,
        'start' : new Date(course.startDate + " " + course.startTime),
        'end' : new Date(course.endDate + " " + course.endTime),
        'desc' : course.description,
        'freq' : course.frequency,
        'author' : course.authorFirstName + " " + course.authorLastName
      }
      i++
    });
    return formattedCourses
  }

 
    const [modalShow, setModalShow] = React.useState(false);
    const [event, setEvent] = React.useState(0);
    const [start, setStart] =React.useState('');
    const [end, setEnd] =React.useState('');
    return(
      <React.Fragment>
        
      <Calendar                
        localizer={localizer}
        events={courseFormat(props)}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        step={30}
        defaultView='month'
        views={['month','week','day']}
        defaultDate={new Date()}
        onSelectEvent={(event)=>handleSelect(event)}
      />

      <MyVerticallyCenteredModal
        style={modalStyle}
        show={modalShow}
        course={event}
        start={start}
        end={end}
        onHide={() => setModalShow(false)}
      />
    </React.Fragment>
      
    )

    function MyVerticallyCenteredModal (props) {
      var course = props.course
      var start = props.start
      var end = props.end
      console.log(course)
      return (
        <Modal
          
          {...props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header>
            <Modal.Title id="contained-modal-title-vcenter">
              Course Information
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>{course.title}</h4>

            <p>start time {start}</p>
            <p>end time {end}</p>
            <p>frequency {course.freq}</p>  
            <p>author {course.author}</p>
            <p>description {course.desc}</p>
            
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={props.onHide}>Close</Button>
          </Modal.Footer>
        </Modal>
      );   
    }
  }

export default Cal
