import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'
import Materialize from "materialize-css";

class CreateCourse extends Component {
    state = {
        title: '',
        description: '',
        startDate: '',
        endDate: '',
        frequency: 0
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        //console.log(this.state)
        this.props.createCourse(this.state)
        this.props.history.push('/')
    }

    componentDidMount() {
        var context = this;
    
        var elems = document.querySelectorAll(".dateset");
        Materialize.Datepicker.init(elems, {
          defaultDate: new Date(),
          format: this.state.format,
          container: "body",
          onSelect: function(date) {
            context.setState({ value: context.state.value });
            console.log(date); // Selected date is logged
          },
          autoClose: true
        });
    }

    render() {
        const {auth} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Create Course</h5>
                    <div className="input-field">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" onChange={this.handleChange}/>
                    </div>
                    <div className="input-field">
                        <label htmlFor="description">Description</label>
                        <textarea id="description" className="materialize-textarea" onChange={this.handleChange}></textarea>
                    </div>
                    <div className="input-field">
                        <label htmlFor="startDate">Start Date</label>
                        <input type="text" id="startDate" className="datepicker" onChange={this.handleChange} required></input>
                    </div>
                    <div className="input-field">
                        <label htmlFor="endDate">End Date</label>
                        <textarea id="endDate" className="materialize-textarea" onChange={this.handleChange}></textarea>
                    </div>
                    <div className="input-field">
                        <label htmlFor="frequency">Frequency</label>
                        <input type="text" id="frequency" onChange={this.handleChange}/>
                    </div>
                    <div className="input-field">
                        <button className="btn blue lighten-1">Create</button>
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state);
    return{
        auth: state.firebase.auth
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        createCourse: (course) => dispatch(createCourse(course))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateCourse)
