const initState = {
    courses: [
        {id: '1', title: 'course1', content: 'this is a course'},
        {id: '2', title: 'course2', content: 'this is another course'},
        {id: '3', title: 'course3', content: 'this is a third course'}
    ]
}

const courseReducer = (state = initState, action) => {
    switch (action.type) {
        case 'CREATE_COURSE':
            console.log('created course', action.course);
            return state;
        case 'CREATE_COURSE_ERROR':
            console.log('create course error', action.err);
            return state;
    }
    return state
}

export default courseReducer
