import React, { Component } from 'react'
import {Redirect} from 'react-router-dom'

class Contact extends Component {
    state = {
        
    }
    getTrainers = (users) =>{
        var trainers = []
        users && users.forEach(user => {
            if(user.userType === "trainer"){
                trainers.push(user)
            }
        })
        return trainers
    }
    getSchedulers = (users) =>{
        var schedulers = []
        users && users.forEach(user => {
            if(user.userType === "scheduler"){
                schedulers.push(user)
            }
        })
        return schedulers
    }
    render() {
        const {auth, users} = this.props;
        const trainers = this.getTrainers(users) 
        const schedulers = this.getSchedulers(users) 
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <div className="card">
                    <div className="card-content">
                        <span className="card-title">Scheduler Contacts</span>
                        {schedulers.map(scheduler => {
                                return (
                                    <option value={scheduler.email}>{skill}</option>
                                )
                            })}
                    </div>
                </div>
                <div className="card">
                    <div className="card-content">
                        <span className="card-title">Trainer Contacts</span>
                        
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth,
        users: state.firestore.ordered.users
    }
}

export default Contact
