import React, {Component} from "react";
import "react-big-calendar/lib/css/react-big-calendar.css";
import {Calendar,DateLocalizer,momentLocalizer,globalizeLocalizer,move,Views,Navigate,components,} from 'react-big-calendar';
import moment from 'moment';
import {Redirect} from 'react-router-dom'

moment.locale('en-GB');
const localizer = momentLocalizer(moment)

class Cal extends Component{
  state = {
    courses: this.props.courses
  }

  CourseInfo = (selectedCourse) => {
    return (
      <Redirect to={{
        pathname: '/courseinfo',
        course: { selectedCourse }
        }}
      />
    )
  }

  courseFormat = () => {
    var courses = this.props.courses
    var formattedCourses = []
    var i = 0
    courses && courses.forEach(course => {
      formattedCourses[i] = {
        'title' : course.title,
        'start' : new Date(course.startDate + " " + course.startTime),
        'end' : new Date(course.endDate + " " + course.endTime),
        'desc' : course.description,
        'freq' : course.frequency,
        'author' : course.authorFirstName + " " + course.authorLastName
      }
      i++
    });
    return formattedCourses
  }

  render(){
    return(
      <Calendar
        localizer={localizer}
        events={this.courseFormat()}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        step={30}
        defaultView='month'
        views={['month','week','day']}
        defaultDate={new Date()}
        // components={{
        //   event: CourseInfo
        // }}
        onSelectEvent={(event) => this.CourseInfo(event)}
      />
    )
  }
}

