import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'
// import "materialize-css/dist/css/materialize.min.css";
// import "materialize-css/dist/js/materialize.min.js";
import { DatePicker } from "react-materialize";

class CreateCourse extends Component {
    state = {
        title: '',
        description: '',
        startDate: '',
        endDate: '',
        frequency: 0
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state)
        this.props.createCourse(this.state)
        this.props.history.push('/')
    }

    render() {
        const {auth} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Create Course</h5>
                    <div className="input-field">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" onChange={this.handleChange} required/>
                    </div>
                    <div className="input-field">
                        <label htmlFor="description">Description</label>
                        <textarea id="description" className="materialize-textarea" onChange={this.handleChange} required></textarea>
                    </div>
                    <div className="input-field">
                        <DatePicker
                            label="Start Date"
                            value=''
                            id="startDate"
                            onChange={(newDate) => {
                                this.setState({
                                    ["startDate"]: newDate
                                  })
                            }}/>
                    </div>
                    <div className="input-field">
                        <DatePicker
                            label="End Date"
                            value=''
                            id="endDate"
                            onChange={(newDate) => {
                                this.setState({
                                    ["endDate"]: newDate
                                  })
                            }}/>
                    </div>
                    <div className="input-field">
                        <label htmlFor="frequency">Frequency</label>
                        <input type="number" id="frequency" name="quantity" min="1" onChange={this.handleChange} required></input>
                    </div>
                    <div className="input-field">
                        <button className="btn blue lighten-1">Create</button>
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state);
    return{
        auth: state.firebase.auth
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        createCourse: (course) => dispatch(createCourse(course))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateCourse)
