import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'

class CreateCourse extends Component {
    state = {
        title: '',
        description: '',
        startDate: '',
        startTime: '',
        endDate: '',
        endTime: '',
        frequency: 0
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }
    getDate = () => {
        var date = new Date();

        var day = date.getDate();
        var month = date.getMonth() + 1;
        var year = date.getFullYear();

        if (month < 10) month = "0" + month;
        if (day < 10) day = "0" + day;

        var today = year + "-" + month + "-" + day;       
        return today;
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.createCourse(this.state)
        this.props.history.push('/')
    }

    
// $(function() {
//     $('input.autocomplete').autocomplete({
//       data: {
//         "Android Development": null,
//         "Apache Spark": null,
//         "ASP.NET": null,
//         "AWS": null,
//         "C": null,
//         "CakePHP": null,
//         "Clojure": null,
//         "CouchDB": null,
//         "Data Science": null,
//         "Django": null,
//         "Elasticsearch": null,
//         "Elm": null,
//         "Express": null,
//         "Flask": null,
//         "Google Analytics": null,
//         "GraphQL": null,
//         "Gulp": null,
//         "HTML 5": null,
//         "Intro to Programming": null,
//         "Java": null,
//         "Jenkins": null,
//         "Kotlin": null,
//         "Latex": null,
//         "Lisp": null,
//         "MATLAB": null,
//         "MySQL": null,
//         "Perl": null,
//         "Polymer": null,
//         "Python": null,
//         "React": null,
//         "Regular Expressions": null,
//         "Rust": null,
//         "Scala": null,
//         "Sketch": null,
//         "SQL": null,
//         "Unity": null,
//         "Unity": null,
//         "Vim": null,
//         "WebGL": null,
//         "Windows Server Administration": null,
//         "Angular": null,
//       },
//       limit: 7, // The max amount of results that can be shown at once. Default: Infinity.
//       onAutocomplete: function(val) {
//         // Callback function when value is autcompleted.
//       },
//       minLength: 1, // The minimum length of the input for the autocomplete to start. Default: 1.
//       });
//     });

    render() {
        const {auth} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Create Course</h5>
                    <div className="input-field">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" onChange={this.handleChange} required/>
                    </div>

                    <div className="input-field">
                        <label htmlFor="description">Description</label>
                        <textarea id="description" className="materialize-textarea" onChange={this.handleChange} required></textarea>
                    </div>

                    <div className="input-field">
                        <label htmlFor="startDate">Start date</label>
                        <input type="date" id="startDate" min={this.getDate()}
                            onChange={this.handleChange} required></input> 
                    </div>

                    <div className="input-field">
                        <label htmlFor="startTime">Start Time (ex: 14:30)</label>
                        <input type="text" id="startTime" onChange={this.handleChange}></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="endDate">End date</label>
                        <input type="date" id="endDate" min={this.getDate()}
                            onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="endTime">End Time (ex: 15:30)</label>
                        <input type="text" id="endTime" onChange={this.handleChange}></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="frequency">Frequency</label>
                        <input type="number" id="frequency" name="quantity" min="1" onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <i class="material-icons prefix">search</i>
                        <input type="text" id="trainer" class="autocomplete"/>
                        <label for="trainer">Search for Trainers</label>
                    </div>

                    <div className="input-field">
                        <button className="btn blue lighten-1">Create</button>
                    </div>

                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        createCourse: (course) => dispatch(createCourse(course))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateCourse)
