import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'
import SelectSearch from 'react-select-search';

class CreateCourse extends Component {
    state = {
        skills: []
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.createCourse(this.state)
        this.props.history.push('/')
    }
    getSkills = (profile) =>{
        var trainers = []
        users.forEach(user => {
            if(user.userType === "trainer"){
                trainers.push(user)
            }
        })
        return trainers
    }

    render() {
        const {auth, users} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        console.log(users)
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Add a Skill</h5>
                    <div className="input-field">
                        <label htmlFor="skill">Skill Name</label>
                        <input type="text" id="skill" onChange={this.handleChange} required/>
                    </div>

                    <div className="input-field">
                        <label htmlFor="skillLvl">Skill Level</label>
                        <input type="number" min="1" max ="4" id="skillLvl" onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <label>Search for Trainers</label><br/><br/>
                        <select id="trainers" className="browser-default" onChange={this.handleChange}>
                            {/* <option value="" disabled selected>Choose a trainer</option> */}
                            {console.log(options())}
                            <SelectSearch options={options()} id="skills" name="skills" placeholder="Choose a Skill" />
                            {/* <button className="btn blue lighten-1" onClick="listTrainers"></button> */}
                            {/* {this.getTrainers(users).map(trainer => {
                                return (
                                    <option value={trainer.id}>{trainer.firstName + " " + trainer.lastName}</option>
                                )
                            })} */}
                        </select>
                    </div>
                    <div className="input-field">
                        <button className="btn blue lighten-1">Create</button>
                    </div>

                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        profile: state.firebase.profile
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        addSKills: (skills) => dispatch(addSKills(skills))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AddSKills)
