import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'
import SelectSearch from 'react-select-search';

class CreateCourse extends Component {
    state = {
        title: '',
        description: '',
        startDate: '',
        startTime: '',
        endDate: '',
        endTime: '',
        frequency: 0,
        trainers: []
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }
    getDate = () => {
        var date = new Date();

        var day = date.getDate();
        var month = date.getMonth() + 1;
        var year = date.getFullYear();

        if (month < 10) month = "0" + month;
        if (day < 10) day = "0" + day;

        var today = year + "-" + month + "-" + day;       
        return today;
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.createCourse(this.state)
        this.props.history.push('/')
    }
    getTrainers = (users) =>{
        var trainers = []
        users && users.forEach(user => {
            if(user.userType === "trainer"){
                trainers.push(user)
            }
        })
        return trainers
    }
    options = (trainers) => {
        var skills =[]
        trainers && trainers.forEach(trainer => {
            trainer.skills && trainer.skills.forEach(skill => {
                if(!skills.includes(skill)){
                    console.log("hi")
                    skills.push({name: skill})
                }
            });
        });
        console.log(skills)
       return skills
    }

    render() {
        const {auth, users} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        console.log(users)
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Create Course</h5>
                    <div className="input-field">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" onChange={this.handleChange} required/>
                    </div>

                    <div className="input-field">
                        <label htmlFor="description">Description</label>
                        <textarea id="description" className="materialize-textarea" onChange={this.handleChange} required></textarea>
                    </div>

                    <div className="input-field">
                        <label htmlFor="startDate">Start date</label>
                        <input type="date" id="startDate" min={this.getDate()}
                            onChange={this.handleChange} required></input> 
                    </div>

                    <div className="input-field">
                        <label htmlFor="startTime">Start Time (ex: 14:30)</label>
                        <input type="text" id="startTime" onChange={this.handleChange}></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="endDate">End date</label>
                        <input type="date" id="endDate" min={this.getDate()}
                            onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="endTime">End Time (ex: 15:30)</label>
                        <input type="text" id="endTime" onChange={this.handleChange}></input>
                    </div>

                    <div className="input-field">
                        <label htmlFor="frequency">Frequency</label>
                        <input type="number" id="frequency" name="quantity" min="1" onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <select id="trainers" className="browser-default" onChange={this.handleChange}>
                            <option value="" disabled selected>Choose a Trainer</option>
                            {this.options(this.getTrainers(users)).map(skill => {
                                return (
                                    <option value={skill.skill}>{skill.skill}</option>
                                )
                            })}
                            {/* <SelectSearch options={this.options(this.getTrainers(users))} id="skills" name="skills" placeholder="Choose a Skill" onChange={this.handleChange}></SelectSearch>
                            <button className="btn blue lighten-1" onClick="listTrainers"></button>
                            {this.getTrainers(users).map(trainer => {
                                return (
                                    <option value={trainer.id}>{trainer.firstName + " " + trainer.lastName}</option>
                                )
                            })} */}
                        </select>
                    </div>
                    <div className="input-field">
                        <button className="btn blue lighten-1">Create</button>
                    </div>

                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth,
        users: state.firestore.ordered.users
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        createCourse: (course) => dispatch(createCourse(course))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateCourse)
