  handleSelectEvent = (course) => {

  }

  courseFormat = () => {
    var courses = this.props.courses
    var formattedCourses = []
    var i = 0
    courses && courses.forEach(course => {
      formattedCourses[i] = {
        'title' : course.title,
        'start' : new Date(course.startDate + " " + course.startTime),
        'end' : new Date(course.endDate + " " + course.endTime),
        'desc' : course.description,
        'freq' : course.frequency,
        'author' : course.authorFirstName + " " + course.authorLastName
      }
      i++
    });
    return formattedCourses
  }

  render(){
    
    const [modalShow, setModalShow] = React.useState(false);
    return(
      
      // <Modal show={showEditModal} onHide={hideModals}>
      //   <Modal.Header closeButton>
      //     <Modal.Title>Edit Calendar Event</Modal.Title>
      //   </Modal.Header>
      //   <Modal.Body>
      //     <p>poop</p>{/* <CourseInfo course={event}/> */}
      //   </Modal.Body>
      // </Modal>

      <>
      
  
      <Calendar
        localizer={localizer}
        events={this.courseFormat()}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        step={30}
        defaultView='month'
        views={['month','week','day']}
        defaultDate={new Date()}
        onSelectEvent={setModalShow(true)}
      />

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />

      </>
    )
  }
}

