import React, { Component } from 'react'
import {connect} from 'react-redux'
import {updateProfile} from '../store/authActions'
import {Redirect} from 'react-router-dom'
import '../styles/form.css'
import Profile from './Profile'

class UpdateAccount extends Component {
    state = {
        firstName: this.props.profile.firstName,
        lastName: this.props.profile.lastName,
        userType: this.props.profile.userType, 
        skills: this.props.profile.skills,
        skill: '',
        skillLvl: 0,
        phoneNo: this.props.profile.phoneNo,
        isTrainer: false
    }
    handleChange = (e) => {
        this.setState({
          [e.target.id]: e.target.value  
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.updateProfile(this.state)
        this.props.history.push('/profile')
    }

    render() {
        const {auth, profile} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <Profile/>
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Sign Up</h5>
                    <div className="input-field">
                        <label htmlFor="firstName">First Name</label>
                        <input type="text" id="firstName" onChange={this.handleChange}/>
                    </div>
                    <div className="input-field">
                        <label htmlFor="lastName">Last Name</label>
                        <input type="text" id="lastName" onChange={this.handleChange}/>
                    </div>
                    <div className="input-field">
                        <label htmlFor="phoneNo">Phone Number (ex: 12345 123456)</label>
                        <input type="text" id="phoneNo" pattern="[0-9]{5} [0-9]{6}" onChange={this.handleChange}/>
                    </div>
                 
                    
                    {this.state.isTrainer &&
                        <div className="input-field">
                            <h5 className="grey-text text-darken-3">Add a Skill</h5>

                            <div className="card">
                                <div className="card-content">
                                    <span className="card-title">Currently Registered Skills</span>
                                    {this.state.skills && this.state.skills.forEach(skill => {
                                        return(<p>{skill}</p>)
                                    })}
                                </div>
                            </div>
                    
                            <div className="input-field">
                                <label htmlFor="skill">Skill Name</label>
                                <input type="text" id="skill" onChange={this.handleChange} required/>
                            </div>
                    
                            <div className="input-field">
                                <label htmlFor="skillLvl">Skill Level</label>
                                <input type="number" min="1" max ="4" id="skillLvl" onChange={this.handleChange} required></input>
                            </div>
                    
                            <div className="input-field">
                                <button type="button" className="btn green lighten-1" onClick={this.addSkill} >Add Skill</button>
                            </div>
                        </div>
                    }
                    
                    <div className="input-field">
                        <button className="btn blue lighten-1">Sign Up</button>
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth,
        profile: state.firebase.profile
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        updateProfile: (user) => dispatch(updateProfile(user))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UpdateAccount)
