      //     <Modal.Title>Edit Calendar Event</Modal.Title>
      //   </Modal.Header>
      //   <Modal.Body>
      //     <p>poop</p>{/* <CourseInfo course={event}/> */}
      //   </Modal.Body>
      // </Modal>

      <Calendar
        localizer={localizer}
        events={this.courseFormat()}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        step={30}
        defaultView='month'
        views={['month','week','day']}
        defaultDate={new Date()}
        onSelectEvent={handleSelectEvent}
      />
    )
  }
}

const handleSelectEvent = (event, e) => {
  setShowEditModal(true);
