import React, {Component} from "react";
import "react-big-calendar/lib/css/react-big-calendar.css";
import {Calendar,DateLocalizer,momentLocalizer,globalizeLocalizer,move,Views,Navigate,components,} from 'react-big-calendar';
import moment from 'moment';
import Modal from "react-bootstrap/Modal";
import{ Button }from 'react-bootstrap'

moment.locale('en-GB');
const localizer = momentLocalizer(moment)

const Cal =(props)=>{

  // CourseInfo = (selectedCourse) => {
  //   console.log(selectedCourse)
  //   return (
  //     <Redirect to={{
  //       pathname: '/courseinfo',
  //       course: { selectedCourse }
  //       }}
  //     />
  //   )
  // }

  function handleSelect (course) {
    setModalShow(true)
    console.log(course)
    return(
      <MyVerticallyCenteredModal
        show={modalShow}
        course={course}
        onHide={() => setModalShow(false)}
      />
    )

  }

  function courseFormat () {
    var courses = props.courses
    var formattedCourses = []
    var i = 0
    courses && courses.forEach(course => {
      formattedCourses[i] = {
        'title' : course.title,
        'start' : new Date(course.startDate + " " + course.startTime),
        'end' : new Date(course.endDate + " " + course.endTime),
        'desc' : course.description,
        'freq' : course.frequency,
        'author' : course.authorFirstName + " " + course.authorLastName
      }
      i++
    });
    return formattedCourses
  }

 
  //useState(variant ? true : false);
    const [modalShow, setModalShow] = React.useState(false);
    return(
      <React.Fragment>
        
      <Calendar
        localizer={localizer}
        events={courseFormat(props)}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        step={30}
        defaultView='month'
        views={['month','week','day']}
        defaultDate={new Date()}
        onSelectEvent={()=>handleSelect(course)}
      />

      
    </React.Fragment>
      
    )

    function MyVerticallyCenteredModal (props) {
      console.log(props)
      return (
        <Modal
          {...props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Modal heading
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>Centered Modal</h4>
            <p>
              Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
              dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
              consectetur ac, vestibulum at eros.
            </p>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={props.onHide}>Close</Button>
          </Modal.Footer>
        </Modal>
      );
    }
  }

export default Cal
