import React, { Component } from 'react'
import Notifications from './Notifications'
import CourseList from './CourseList'
import {connect} from 'react-redux'
import {firestoreConnect} from 'react-redux-firebase'
import {compose} from 'redux'
import {Redirect} from 'react-router-dom'
import Cal from './Cal'

class Dashboard extends Component {
    checkUser = (id, users) => {
        for(var i =0; i < users.length; i++){
            if(id === users[i].id && users[i].userType === "trainer"){
                return("trainer")
            }
            else {
                return ("scheduler")
            }
        }
    }
    render() {
        const {courses, auth, notifications, users} = this.props;
        console.log(auth)
        console.log(users)
        if (!auth.uid) return <Redirect to='/signin' />
        if (users && this.checkUser(auth.uid, users) === 'trainer') return <Redirect to='/trainer' />
        return (
            <div className="dashboard container">
                {/* <div className="row">
                    <div className="col s12 m6">
                        <CourseList courses={courses} />
                    </div>
                    <div className="col s12 m5 offset-m1">
                        <Notifications notifications={notifications}/>
                    </div>
                </div> */}
                <Cal courses={courses} />
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        courses: state.firestore.ordered.courses,
        auth: state.firebase.auth,
        notifications: state.firestore.ordered.notifications,
        users: state.firestore.ordered.users
    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        {collection: 'courses', orderBy: ['createdAt', 'desc']},
        {collection: 'notifications', orderBy: ['time', 'desc']},
        {collection: 'users'}
    ])
)(Dashboard)
