import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createCourse} from '../store/courseActions'
import {Redirect} from 'react-router-dom'
import SelectSearch from 'react-select-search';

class CreateCourse extends Component {
    state = {
        skills: []
    }
    handleChange = (e) => {
        if(this.target.id === "skill"){
            this.setState({
                [skills]: {skill: e.target.value, skillLvl: ''}  
              })
        }
        else {
            this.setState({
                [skills]: {skill: this.state.skills.skill, skillLvl: e.target.value}  
              })
        }
        
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.addSkill(this.state)
        this.props.history.push('/trainer')
    }

    render() {
        const {auth, profile} = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        console.log(profile)
        var skills = this.getSkills(profile)
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Add a Skill</h5>

                    <div className="card">
                        <div className="card-content">
                            <span className="card-title">Currently Registered Skills</span>
                            {profile.skills && profile.skills.forEach(skill => {
                                return(<p>{skill}</p>)
                            })}
                        </div>
                    </div>

                    <div className="input-field">
                        <label htmlFor="skill">Skill Name</label>
                        <input type="text" id="skill" onChange={this.handleChange} required/>
                    </div>

                    <div className="input-field">
                        <label htmlFor="skillLvl">Skill Level</label>
                        <input type="number" min="1" max ="4" id="skillLvl" onChange={this.handleChange} required></input>
                    </div>

                    <div className="input-field">
                        <button className="btn blue lighten-1">Add Skill</button>
                    </div>

                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth,
        profile: state.firebase.profile
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        addSkill: (skills) => dispatch(addSkill(skills))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AddSKills)
